use crate::token::Token;
use std::fmt;

#[derive(Debug)]
pub struct CognosError {
    pub kind: ErrorKind,
    pub line: usize,
    pub message: String,
    pub hint: Option<String>,
}

#[derive(Debug)]
pub enum ErrorKind {
    Parse,
    Runtime,
    Type,
}

